$(document).ready(function(){var a=$("#filegroup_table").DataTable({sAjaxSource:listUrl,sAjaxDataProp:"",aoColumns:[{mData:function(b){return'<a href="'+editGroupFileUrl+b.id+'">'+b.name+"</a>"}}],columnDefs:[{render:function(b){return moment(b).format("MMM DD, YYYY hh:mm:ss A")},targets:1},{render:function(){return'<button class="btn btn-danger btn-xs delete" data-placement="top" data-toggle="tooltip" title="Delete File"><span class="glyphicon glyphicon-trash"></span></button>'},orderable:false,bSearchable:false,targets:2}],order:[[0,"asc"]]});$("#filegroup_table tbody").on("click","tr td .delete",function(){var d=$(this).parents("tr")[0];var c=a.row(d);var b=c.data();$("#confirm_delete").find(".modal-title").text("Delete File: "+b.name);$("#del_btn").data("id",c.index());$("#confirm_delete").modal("toggle")});$(document).on("click","#del_btn",function(c){var f=$("#del_btn").data("id");var d=a.row(f);var b=d.data();$.ajax({url:deleteUrl,data:{id:b.id},type:"POST",success:function(){d.remove().draw()}})});$("body").tooltip({selector:'[data-toggle="tooltip"]'})});